package test;
import java.util.Scanner;
public class patternofalphabets {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4;                                                                                
		int d=1;
		int c = 1;
int e=1;		
		for (;a>=d;--a) {

			for(int j=1;j<=e;j++) { 	
				System.out.print(" ");
			}
			e++;
			for(int k=1;k<=a;k++) {
				System.out.print("*");
			}
			c++;
			System.out.println("");
		}


	}
}
